import { StringType, EmptyAction, PayloadAction, PayloadMetaAction } from './types';
export declare function action<T extends StringType>(type: T): EmptyAction<T>;
export declare function action<T extends StringType, P = undefined>(type: T, payload: P): PayloadAction<T, P>;
export declare function action<T extends StringType, P = undefined, M = undefined>(type: T, payload: P, meta: M): PayloadMetaAction<T, P, M>;
